import './App.css';
import React, { Component } from 'react'
import NewForm from './NewForm'
import Nav from './Nav'

let baseUrl = "http://localhost:8000/api/v1/"

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      posts:[],
      modalOpen: false,
      postsToBeEdited:{},
      text:"",
      user:"",
      userLoggedIn: false 
    }
  }

  getPosts = () => {
    fetch(baseUrl + "/home/",{
      // credentials:"include"
    })
    .then(res => {
      // eslint-disable-next-line no-cond-assign
      if(res.status=200){
        return res.json
      } else {
        return []
      }
    })
  }

  createPost = (newPost) => {
    const copyPosts = [...this.state.posts]
    copyPosts.push(newPost)
    this.setState({
      posts:copyPosts
    })
  }

}
export default App;
