import './App.css';
import React, { Component } from 'react'
import NewForm from './NewForm'
import Nav from './Nav'

let baseUrl = "http://localhost:8000/api/v1/"

class App extends Component {
  constructor(props){
    super(props)

    this.state = {
      posts:[],
      modalOpen: false,
      postToBeEdited:{},
      text:"",
      user:"",
      userLoggedIn: false,
      userLoginOpen:false,
      userRegisterOpen:false 
    }
  }

  getPosts = () => {
    fetch(baseUrl + "/home/",{
      // credentials:"include"
    })
    .then(res => {
      if(res.status === 200){
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({posts:data["data"]})
    })
  }

  createPost = (newPost) => {
    const copyPosts = [...this.state.posts]
    copyPosts.push(newPost)
    this.setState({
      posts:copyPosts
    })
  }

  loginUser = async (e) => {
    e.preventDefault()
    const url = baseUrl + '/user/login'
    const loginBody = {
      username: e.target.username.value,
      password: e.target.password.value
    }
    try {

      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify(loginBody),
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: "include"
      })

      console.log(response)
      console.log("BODY: ",response.body)

      if (response.status === 200) {
        this.getPosts()
      }
    }
    catch (err) {
      console.log('Error => ', err);
    }
  }

  register = async (e) => {
    e.preventDefault()
    const url = baseUrl + '/user/register'
    try {
      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
          username: e.target.username.value,
          password: e.target.password.value,
          email: e.target.email.value
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      if (response.status === 200) {
        this.getPosts()
      }
    }
    catch (err) {
      console.log('Error => ', err);
    }
  }

  deletePost = (id) => {
    fetch(baseUrl + `home/` + id, {
    method: 'DELETE',
    // credentials: "include"
  }).then( res => {
    const findIndex = this.state.posts.findIndex(post => post["id"] === id)
    console.log(findIndex)
    const copyPosts = [...this.state.posts]
    copyPosts.splice(findIndex, 1)
    this.setState({
      posts: copyPosts
    })
  })
  }

  handleSubmit = async (e) => {
    e.preventDefault()
    const url = baseUrl + 'home/' + this.state.postToBeEdited["id"]
    try{
      const response = await fetch( url , {
        method: 'PUT',
        body: JSON.stringify({
          text: e.target.text.value
        }),
        headers: {
          'Content-Type' : 'application/json'
        }
      })

      if (response.status === 200){
        const updatedPost = await response.json()
        const findIndex = this.state.posts.findIndex(post => post["id"] === updatedPost["id"])
        const copyPosts = [...this.state.posts]
        copyPosts[findIndex] = updatedPost
        this.setState({
          posts: copyPosts,
          modalOpen:false
        })
      }
    }
    catch(err){
      console.log('Error => ', err);
    }
  }

  handleChange = (e)=>{
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  showEditForm = (post)=>{
    this.setState({
      modalOpen:true,
      text: post.text,
      postToBeEdited:post
    })
  }

  componentDidMount() {
    this.getPosts()
  }

  render() {
    return (
      <div className="App">
        <Nav loginUser={this.loginUser} register={this.register}/>
        <h1>Hello World!</h1>

        <nav>
            <form onSubmit={props.loginUser}>
                <strong>Login </strong>
                <label htmlFor="name">Username: </label>
                <input type="text" id="name" name="username"/>
                <label htmlFor="name">Password: </label>
                <input type="text" id="password" name="password"/>
                <input type="submit" value="login" />
            </form>
            OR
            <form onSubmit={props.register}>
                <strong>Register </strong>
                <label htmlFor="name">Username: </label>
                <input type="text" id="name" name="username"/>
                <label htmlFor="name">Password: </label>
                <input type="text" id="password" name="password"/>
                <input type="submit" value="signup" />
            </form>
        </nav>
        <NewForm baseUrl={baseUrl}  createPost={ this.createPost }/>
        <table>
          <tbody>
            { this.state.posts.map((post, i) => {
                return (
                  <tr key={i}>
                    <td>{post.user["username"]}</td>
                    <td>{post.text}</td>
                    <td onClick={() => { this.showEditForm(post)}}>Edit</td>
                    <td onClick={() => { this.deletePost(post["id"])}}>Delete</td> 
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        <br/>
          {
            this.state.modalOpen &&

            <form onSubmit={this.handleSubmit}>
              <label>Text: </label>
              <input name="text" type="text"  value={this.state.text} onChange={this.handleChange}/>
              <button>Submit</button>
            </form>
          }
      </div>
    );
  }

}
export default App;
