import './App.css';
import React, { Component } from 'react'
import NewForm from './NewForm'
import Nav from './Nav'

let baseUrl = "http://localhost:8000/api/v1/home/"

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      holidays: [],
      modalOpen: false,
      holidayToBeEdited:{},
      description:'',
      name: '',
      userLoggedIn: false
    }
    //if you do not use an arrow function for getHolidays()
    // this.getHolidays = this.getHolidays.bind(this)
  }

  getHolidays = () => {
    // fetch to the backend
    fetch(baseUrl + "/holidays",{
    })
    .then(res => {
      if(res.status === 200) {
        return res.json()
      } else {
        return []
      }
    }).then(data => {
      console.log(data)
      this.setState({ holidays: data })
    })
  }

  addHoliday = (newHoliday) => {
    const copyHolidays = [...this.state.holidays]
    copyHolidays.push(newHoliday)
    this.setState({
      holidays: copyHolidays,
    })
  }

  loginUser = async (e) => {
    console.log('loginUser')
    e.preventDefault()
    const url = baseUrl + '/users/login'
    const loginBody = {
      username: e.target.username.value,
      password: e.target.password.value
    }
    try {

      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify(loginBody),
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: "include"
      })

      console.log(response)
      console.log("BODY: ",response.body)

      if (response.status === 200) {
        this.getHolidays()
      }
    }
    catch (err) {
      console.log('Error => ', err);
    }
  }

  register = async (e) => {
    e.preventDefault()
    const url = baseUrl + '/users/signup'
    try {
      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
          username: e.target.username.value,
          password: e.target.password.value
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      })
      if (response.status === 200) {
        this.getHolidays()
      }
    }
    catch (err) {
      console.log('Error => ', err);
    }
  }


  toggleCelebrated = (holiday) => {
    // console.log(holiday)
    fetch(baseUrl + '/holidays/' + holiday._id, {
      method: 'PUT',
      body: JSON.stringify({celebrated: !holiday.celebrated}),
      headers: {
        'Content-Type': 'application/json'
      },
      credentials: "include"
    }).then(res => res.json())
    .then(resJson => {
      // console.log(resJson)
      const copyHolidays = [...this.state.holidays]
      const findIndex = this.state.holidays.findIndex(
        holiday => holiday._id === resJson._id)
      copyHolidays[findIndex].celebrated = resJson.celebrated
      this.setState({
        holidays: copyHolidays
      })
    })
  }

  deleteHoliday = (id) => {
      // console.log(id)
      fetch(baseUrl + '/holidays/' + id, {
      method: 'DELETE',
      credentials: "include"
    }).then( res => {
      // console.log(res)
      // if I checked for a 200 response code
      const findIndex = this.state.holidays.findIndex(holiday => holiday._id === id)
      const copyHolidays = [...this.state.holidays]
      copyHolidays.splice(findIndex, 1)
      this.setState({
        holidays: copyHolidays
      })
    })
  }

  addLike = (holiday) => {
    // console.log(holiday)
    fetch(baseUrl + '/holidays/' + holiday._id, {
      method: 'PUT',
      body: JSON.stringify({ likes: holiday.likes + 1}),
      headers: {
        'Content-Type': 'application/json'
      },
      credentials: "include"
    }).then(res => res.json())
    .then(resJson => {
      // console.log(resJson)
      const copyHolidays = [...this.state.holidays]
      const findIndex = this.state.holidays.findIndex(holiday => holiday._id === resJson._id)
      copyHolidays[findIndex].likes = resJson.likes
      this.setState({
        holidays: copyHolidays
      })
    })
  }

  // for more on async functions
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Statements/async_function
  handleSubmit = async (e) => {
    e.preventDefault()
    const url = baseUrl + '/holidays/' + this.state.holidayToBeEdited._id
    try{
      const response = await fetch( url , {
        method: 'PUT',
        body: JSON.stringify({
          name: e.target.name.value,
          description: e.target.description.value
        }),
        headers: {
          'Content-Type' : 'application/json'
        },
        credentials: "include"
      })

      if (response.status === 200){
        const updatedHoliday = await response.json()
        //console.log(updatedHoliday)
        const findIndex = this.state.holidays.findIndex(holiday => holiday._id === updatedHoliday._id)
        const copyHolidays = [...this.state.holidays]
        copyHolidays[findIndex] = updatedHoliday
        this.setState({
          holidays: copyHolidays,
          modalOpen:false
        })
      }
    }
    catch(err){
      console.log('Error => ', err);
    }
  }

 handleChange = (e)=>{
   this.setState({
     [e.target.name]: e.target.value
   })
 }

  showEditForm = (holiday)=>{
    this.setState({
      modalOpen:true,
      name: holiday.name,
      description: holiday.description,
      holidayToBeEdited:holiday
    })
  }


  // component lifecycle flowchart
  // https://levelup.gitconnected.com/componentdidmakesense-react-lifecycle-explanation-393dcb19e459
  componentDidMount() {
    this.getHolidays()
  }

  render() {
    return (
      <div className="App">
        <Nav loginUser={this.loginUser} register={this.register}/>
        <h1>Holidays! Celebrate! </h1>
        <NewForm baseUrl={baseUrl} addHoliday={ this.addHoliday }/>
        <table>
          <tbody>
            { this.state.holidays.map((holiday, i) => {
                return (
                  <tr key={holiday._id}>
                    <td onDoubleClick={() => this.toggleCelebrated(holiday)}
                     className={ holiday.celebrated ? 'celebrated' : null }>
                     { holiday.name }
                    </td>
                    <td key={i}> {holiday.description} </td>
                    <td>{holiday.likes}</td>
                    <td onClick={() => { this.addLike(holiday)}}>LIKE</td>
                    <td onClick={() => { this.showEditForm(holiday)}}>Show Edit Form</td>
                    <td onClick={() => this.deleteHoliday(holiday._id)}>X</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
        <br/>
          {
            this.state.modalOpen &&

            <form onSubmit={this.handleSubmit}>
              <label>Name: </label>
              <input name="name" value={this.state.name} onChange={this.handleChange}/> <br/>

              <label>Description: </label>
              <input name="description" value={this.state.description} onChange={this.handleChange}/>

              <button>submit</button>

            </form>
          }
      </div>
    );
  }
}

export default App;
